import React from 'react';
import { Menu } from '@headlessui/react';
import { Icon } from 'components/Icons';

export default function User() {
  const person = {
    name: 'ErenYalcn',
    avatar: 'https://i.scdn.co/image/ab6775700000ee85d42d00c70faf10da7c991fa8'
  };

  return (
    <Menu as="nav" className="relative">
      {({ open }) => (
        <>
          <Menu.Button className={`flex items-center gap-x-2 rounded-full p-1 ${open ? 'bg-active' : 'bg-brenk'}`}>
            <img className='w-7 h-7 rounded-full' src={person.avatar} alt="Avatar" />
            <span className='text-sm font-semibold'>{person.name}</span>
            <span className={open ? 'transform rotate-180' : ''}>
              <Icon name="dir" />
            </span>
          </Menu.Button>
          <Menu.Items className="absolute p-1 top-full right-0 w-48 bg-active rounded translate-y-2 z-10">
            <Menu.Item>
              {({ active }) => (
                <a
                  className={`h-10 flex px-2 text-sm font-semibold justify-between text-white opacity-80 items-center ${active && 'bg-menubg'}`}
                  href="/#"
                >
                  Account
                  <Icon name="external" />
                </a>
              )}
            </Menu.Item>
            <Menu.Item>
              {({ active }) => (
                <a
                  className={`h-10 flex px-2 text-sm font-semibold justify-between text-white opacity-80 items-center ${active && 'bg-menubg'}`}
                  href="/#"
                >
                  Profile
                </a>
              )}
            </Menu.Item>
            <Menu.Item>
              {({ active }) => (
                <a
                  className={`h-10 flex px-2 text-sm font-semibold justify-between text-white opacity-80 items-center ${active && 'bg-menubg'}`}
                  href="/#"
                >
                  Sign out
                </a>
              )}
            </Menu.Item>
          </Menu.Items>
        </>
      )}
    </Menu>
  );
}
